import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardContent, CardMedia, Typography, Button, Grid } from '@mui/material';

import { EventList } from '../component/Event/EventList'; // Ensure EventList is a list of events

// Custom styles
const useStyles = mak((theme) => ({
  root: {
    padding: theme.spacing(4),
    backgroundColor: '#f5f5f5',
  },
  card: {
    maxWidth: 345,
    margin: theme.spacing(2),
  },
  media: {
    height: 140,
  },
  button: {
    marginTop: theme.spacing(2),
  },
  link: {
    textDecoration: 'none',
    color: 'inherit',
  },
}));

const HomePage = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        Upcoming Events
      </Typography>
      
      <Grid container spacing={4} justifyContent="center">
        <Grid item xs={12} sm={6} md={4}>
          <Card className={classes.card}>
            <CardMedia
              className={classes.media}
              image="https://source.unsplash.com/random" // Replace with your image URL
              title="Event Image"
            />
            <CardContent>
              <Typography variant="h5" component="div">
                Event Title
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Event Description
              </Typography>
            </CardContent>
            <Button className={classes.button} variant="contained" color="primary" fullWidth>
              View Details
            </Button>
          </Card>
        </Grid>

        {/* Repeat Grid item for more events */}
      </Grid>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <Link to="/create-event" className={classes.link}>
          <Button variant="contained" color="secondary">
            Create New Event
          </Button>
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
